library(dplyr)

GLOBAL_TOP_10 &lt;- read_tsv(GLOBAL_TOP_10_FILENAME, na = "N/A")

COUNTRY_TOP_10 &lt;- read_tsv(COUNTRY_TOP_10_FILENAME, na = "N/A")

